def part1(input):
    floor = 0
    for i in input:
        if i == "(":
            floor = floor + 1
        if i == ")":
            floor = floor - 1
        #print(i)
    return floor

def part2(input):
    floor = 0
    pos = 1
    for i in input:
        if i == "(":
            floor = floor + 1
        if i == ")":
            floor = floor - 1
        if floor == -1:
            return pos
        pos = pos + 1
        #print(i)
    return pos

